// Robust function for n8n - finds first two arrays-of-strings in items
function findStringArrays(items) {
  const arrays = [];
  for (const it of items) {
    const json = it.json || {};
    // если сам айтем — массив строк (на всякий случай)
    if (Array.isArray(json) && json.every(v => typeof v === 'string')) {
      arrays.push(json);
      if (arrays.length >= 2) break;
    }
    // иначе проверим поля объекта
    for (const key of Object.keys(json)) {
      const val = json[key];
      if (Array.isArray(val) && val.length > 0 && val.every(v => typeof v === 'string')) {
        arrays.push(val);
        break; // перейдём к следующему айтему
      }
    }
    if (arrays.length >= 2) break;
  }
  return arrays;
}

