// In n8n, when you have 2 inputs, you need to access them separately
// Assuming input 0 has 'output' and input 1 has 'assistant_answer'

const items = $input.all();

// Try to find which input has what
let outputsData = null;
let assistantsData = null;

// Check all inputs
for (let i = 0; i < items.length; i++) {
  const item = items[i].json;
  if (item.output) {
    outputsData = item.output;
  }
  if (item.assistant_answer) {
    assistantsData = item.assistant_answer;
  }
}

// Debug: show what we found
if (!outputsData || !assistantsData) {
  return {
    error: "Missing data",
    foundOutputs: !!outputsData,
    foundAssistants: !!assistantsData,
    numberOfInputs: items.length,
    input0Keys: items[0] ? Object.keys(items[0].json) : null,
    input1Keys: items[1] ? Object.keys(items[1].json) : null
  };
}

// Extract all outputs
const outputs = [];
Object.keys(outputsData).forEach(key => {
  const index = parseInt(key);
  if (!isNaN(index)) {
    outputs.push({ index, data: outputsData[key] });
  }
});
outputs.sort((a, b) => a.index - b.index);

// Extract all assistant answers
const assistants = [];
Object.keys(assistantsData).forEach(key => {
  const index = parseInt(key);
  if (!isNaN(index)) {
    assistants.push({ index, data: assistantsData[key] });
  }
});
assistants.sort((a, b) => a.index - b.index);

if (assistants.length === 0 || outputs.length === 0) {
  return { 
    error: "No data found", 
    outputs: outputs.length, 
    assistants: assistants.length 
  };
}

// Calculate x/y = outputs per assistant
const outputsPerAssistant = Math.floor(outputs.length / assistants.length);

// Group outputs with their corresponding assistant
const allData = {};

for (let i = 0; i < assistants.length; i++) {
  const startIdx = i * outputsPerAssistant;
  const endIdx = (i === assistants.length - 1) 
    ? outputs.length  // Last assistant gets all remaining outputs
    : startIdx + outputsPerAssistant;

  const outputsGroup = {};

  for (let j = startIdx; j < endIdx; j++) {
    outputsGroup[`output[${outputs[j].index}]`] = outputs[j].data;
  }

  allData[`assistant_answer[${assistants[i].index}]`] = {
    value: assistants[i].data,
    outputs: outputsGroup
  };
}

return { assistant_answer: allData };

