for (const [key, data] of Object.entries(assistantAnswers)) {
  formattedText += `=== ${key} ===\n`;
  formattedText += `Answer: ${data.value}\n\n`;

  if (data.outputs) {
    formattedText += "Evaluations:\n";
    for (const [outputKey, outputValue] of Object.entries(data.outputs)) {
      formattedText += `${outputKey}: ${outputValue}\n\n`;
    }
  }
  formattedText += "\n";
}

return { json: { formattedData: formattedText } };
